sourcesContent:
import express from 'express';
import mapRoute from './routes/index';

const app = express();
const PORT = process.env.PORT || 5000;

mapRoute(app);
app.listen(PORT, () => {
  console.log(`Server listening on PORT ${PORT}`);
});

export default app;
module.exports = app;

